# Import settings
Function Get-Settings ([string]$fileName) {
       $ini = @{}
       switch -regex -file $fileName {
              "^\[(.+)\]$" {                # recognize a section
                     $section = $matches[1]
                     $ini[$section] = @{}
              }
              "^\s*([^#]+?)\s*=\s*(.*)" {   # recognize a property
                     $name,$value = $matches[1..2]
                     if (!(Test-path variable:\section)) {
                           $section = "-unknown-"
                           $ini[$section] = @{}
                     }
                     $ini[$section][$name] = $value.trim()
              }
       }
       $ini
} 
